def hisdb(dbname,ticker):
    conn=connectdb(dbname)
    cursor = conn.cursor()
    cursor.execute ("DROP TABLE IF EXISTS %s" %ticker)
    cursor.execute( """CREATE TABLE \
      %s(
      ID   INT PRIMARY KEY AUTO_INCREMENT,
      Date  DATE ,
      Open   FLOAT ,
      High   FLOAT ,
      Low     FLOAT,
      Close    FLOAT ,
      Volume INT ,
      Adj_Close FLOAT,
      ma20 float,
      ma50 float,
      ma200 float
      )
      """  % ticker)
    data = gethistory(ticker, start, end)[1:]

    if len(data)>20:
        for i in range(len(data)-19):
            sum=0
            for j in range(20):
                sum+=float(data[i+j][6])
            data[i].append(sum/20)
